export const getProduct = (opts: { id: string }) =>
	Promise.resolve(
		new Response(JSON.stringify(products.find((p) => p.id === opts.id))),
	);

export const getProducts = (opts: { exclude: string[] }) =>
	Promise.resolve(
		new Response(
			JSON.stringify(products.filter((p) => !opts.exclude.includes(p.id))),
		),
	);
